mapper.select:
SELECT id
FROM MENU_GROUP
WHERE store_id = #{storeId}
  AND name = #{name} LIMIT 1;

mapper.update:
UPDATE MENU_GROUP
SET name = #{menuGroup.name},
    info = #{menuGroup.info}
WHERE id = #{id};

update.foreach:
UPDATE MENU_GROUP
SET priority = #{index}+1
WHERE id = #{menuGroup.id}

mapper.select:
SELECT * FROM MENU_GROUP
WHERE id IN

    #{menuGroup.id}
