mapper.update:
UPDATE ORDERS
SET status = #{orderStatus}
WHERE id = #{orderId};

mapper.select:
SELECT id
FROM ORDERS
WHERE id >= #{startId}
  AND user_id = #{userId}

SELECT status, order_price, created_at, store_id, json_extract(history, '$.menuCount') AS menu_count, json_extract(history, '$.menuList[0].menuName') AS menu_name FROM ORDERS WHERE id IN
#{id}
ORDER BY created_at DESC LIMIT 10;

mapper.update:
UPDATE
    ORDERS
SET status     = 'IN_DELIVERY',
    start_time = NOW(),
    rider_id   = #{riderId}
WHERE id = #{orderId}

UPDATE
    ORDERS
SET status   ='FINISH_DELIVERY',
    end_time = NOW()
WHERE id = #{orderId}
  AND rider_id = #{riderId}

mapper.select:
SELECT id
FROM ORDERS
WHERE rider_id = #{riderId}

    AND id < #{startId}

ORDER BY end_time DESC
LIMIT 10

SELECT o.id, o.status, s.address_id, s.name, s.detail_address, o.delivery_address, u.phone_number, o.start_time, o.end_time FROM ORDERS o JOIN STORE s ON o.id IN
#{id}
AND o.rider_id = #{riderId} AND s.id = o.store_id JOIN USER u ON u.id = o.user_id ORDER BY o.end_time DESC
